package AppServlet.CourseServlet;import Models.Course;import Repository.CourseRepository;import jakarta.servlet.RequestDispatcher;import jakarta.servlet.ServletException;import jakarta.servlet.annotation.WebServlet;import jakarta.servlet.http.HttpServlet;import jakarta.servlet.http.HttpServletRequest;import jakarta.servlet.http.HttpServletResponse;import java.io.IOException;import java.sql.SQLException;import java.util.ArrayList;import java.util.List;@WebServlet(urlPatterns = {"/courseManage"})public class CourseManageServlet extends HttpServlet {    @Override    protected void doGet(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {        List<Course> courses;        try {            courses = new ArrayList<>(CourseRepository.getInstance().getAllCourse());            req.setAttribute("course",courses);            req.setAttribute("success",true);        } catch (SQLException e) {            req.setAttribute("success",false);            req.setAttribute("course",new ArrayList<Course>());            throw new RuntimeException(e);        }        RequestDispatcher dispatcher = this.getServletContext().getRequestDispatcher("/WEB-INF/views/CourseManage.jsp");        dispatcher.forward(req, resp);    }}